#-----to put stuffs together
def run(input)
  while true
    case input
    when '1'
      puts _1_how_many_handle
    when '2'
      puts _2_shortest_handle
    when '3'
      puts _3_five_handle
    when '4'
      puts _4_start_one_cap
    when '5'
      puts _5_count_one_cap
    when "6"
      puts _6_little_to_bigone
    when "7"
      puts _7_position("@epenser")
    when '8'
      
    end
    puts "-------------------------------------"
    puts "Voulez vous voir d'autres choses ?"
    puts '>> '
    input = gets.chomp
  end
end

#-----on fait tourner le bazar
def perform
  run(user_interface)
end

